package week1.day1;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int fact=1;
		System.out.println("factorial value upto 5");
		for (int i = 1; i <= 5; i++) {
			fact=fact*i;
			System.out.println(fact);
			
		}
		
  
	}

}
